import { useEffect, useState } from "react";

function Prices(props) {
    const [currency, setCurrency] = useState('USD') 
    const {bpi} = props.bpi
    let list = '';
    if (currency === "EUR") {
            list = <li className='list-group-item'>
            Bitcoin Rate For {bpi.USD.description} : {bpi.USD.rate } <span className="badge badge-primary">{bpi.USD.code}</span>
        </li>
    }
    else {
        list = <li className='list-group-item'>
        Bitcoin Rate For {bpi.currency.description} : {bpi.currency.rate } <span className="badge badge-primary">{bpi.currency.code}</span>
    </li>

    }


    return (
        <div>
            <p>Check current Bitcoin Rate </p>
            {list}
            {/* {props.bpi.time.updated} */}

        </div>
    )

}
export default Prices;
